mapper.select:
select list_no, list_name, list_img, list_open, user_id
from list
where user_id = #{userId}

select list_no, list_name, list_img, list_open, user_id
from list
where list_no = #{listNo}

select place_no, place_name, place_url, place_address_name, place_road_address_name,place_phone, place_x, place_y, list_no 
from place
where list_no = #{listNo}

mapper.update:
update list
set list_name = #{listName}, list_img = #{listImg}, list_open = #{listOpen}
where list_no = #{listNo}
